sequence.script:

var quotaUser = mc.getProperty('uri.var.quotaUser');
var searchAndReplaceDetails = mc.getProperty('uri.var.searchAndReplaceDetails');
var prettyPrint = mc.getProperty('uri.var.prettyPrint');
var excludeDetails = mc.getProperty('uri.var.excludeDetails');
var lowercaseDetails = mc.getProperty('uri.var.lowercaseDetails');
var advancedDetails = mc.getProperty('uri.var.advancedDetails');
var uppercaseDetails = mc.getProperty('uri.var.uppercaseDetails');
var callback = mc.getProperty('uri.var.callback');
var userIp = mc.getProperty('uri.var.userIp');
var fields = mc.getProperty('uri.var.fields');
var includeDetails = mc.getProperty('uri.var.includeDetails');
var jsonPayload = mc.getPayloadJSON();
var urlQuery = '';
if (callback != null && callback != "") {
   urlQuery += 'callback=' + callback + '&';
}
if (prettyPrint != null && prettyPrint != "") {
   urlQuery += 'prettyPrint=' + prettyPrint + '&';
}
if (quotaUser != null && quotaUser != "") {
   urlQuery += 'quotaUser=' + quotaUser + '&';
}
if (userIp != null && userIp != "") {
   urlQuery += 'userIp=' + userIp + '&';
}
if (fields != null && fields != "") {
   urlQuery += 'fields=' + fields + '&';
}
// Add a leading '?' and remove the trailing '&'
if (urlQuery != "") {
   urlQuery = '?' + urlQuery.toString().substring(0, urlQuery.toString().lastIndexOf("&"));
   mc.setProperty('uri.var.urlQuery', urlQuery);
}
if (advancedDetails != null && advancedDetails != ""){
   jsonPayload.advancedDetails = eval("(" + advancedDetails + ")");
}
if (excludeDetails != null && excludeDetails != ""){
   jsonPayload.excludeDetails = eval("(" + excludeDetails + ")");
}
if (includeDetails != null && includeDetails != ""){
   jsonPayload.includeDetails = eval("(" + includeDetails + ")");
}
if (lowercaseDetails != null && lowercaseDetails != ""){
   jsonPayload.lowercaseDetails = eval("(" + lowercaseDetails + ")");
}
if (searchAndReplaceDetails != null && searchAndReplaceDetails != ""){
   jsonPayload.searchAndReplaceDetails = eval("(" + searchAndReplaceDetails + ")");
}
if (uppercaseDetails != null && uppercaseDetails != ""){
   jsonPayload.uppercaseDetails = eval("(" + uppercaseDetails + ")");
}
mc.setPayloadJSON(jsonPayload);
